export const authEndpoint = 
'https://accounts.spotify.com/authorize/';

const redirectUri = 'http://localhost:3000/';

const clientId = '377e5bf3054249ffb055e7a63ff091c8';

// const clientSecret = 'e70ea7c0d2784a08a05a358fa12071c1';

const scopes = [
    'user-read-currently-playing',
    'user-read-recently-played',
    'user-read-playback-state',
    'user-top-read',
    'user-modify-playback-state'
]

export const getTokenFromUrl = () => {
    return window.location.hash
    .substring(1)
    .split('&')
    .reduce((initial, item) => {
        let parts = item.split('=');
        initial[parts[0]] =
        decodeURIComponent(parts[1]);

        return initial;
    }, {});
}

export const loginUrl = 
`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=
${scopes.join('%20')}&response_type=token&show_dialog=true`;
